import React, {Component} from "react";
import Background from "../backgroundComponent/Background";
import Header from "../headerComponent/Header";
import Area from "../areaComponent/Area";
import {Spring, config} from "react-spring/renderprops"
import Button from "@material-ui/core/Button";
import ReactDOM from 'react-dom';
import posed from 'react-pose';
import { tween } from 'popmotion';
import { interpolate } from 'flubber';


const paths = {
    start:
        "M0 1080L604.83 1080L1920 1080L1920 0L1368.9 0L0 0L0 1080Z",
    end :
        "M0 1080L518.75 1080L917.64 540L1164.84 426.45L1468.22 0L0 0L0 1080Z",
}

const pathIds = Object.keys(paths);

const morphTransition = ({ from, to }) =>
    tween({
        from: 0,
        to: 1
    }).pipe(interpolate(from, to));

const Icon = posed.path(
    pathIds.reduce((config, id) => {
        config[id] = {
            d: paths[id],
            transition: morphTransition
        };

        return config;
    }, {})
);

class StartAnimation extends Component{
    goToNext = () => {
        const { pathIndex } = this.state;
        const nextIndex = pathIndex + 1;
        this.setState({
            pathIndex: nextIndex > pathIds.length - 1 ? 0 : nextIndex
        });
    };

    render(){
        return(
            <div className="testing">
                <button className="startButton"
                        style={{position: "absolute"}}
                        onClick={this.goToNext}>
                    Click me
                </button>
            </div>
        );
    }
}



