import React, {Component} from "react"
import {Spring} from "react-spring/renderprops-universal";
import about from "../../assets/img/about_me.jpg";

class LinkCard extends Component{
    constructor(props) {
        super(props);
        this.state = {hovered: false};
    }
    render() {
        return(
            <Spring
                from = {{opacity: 0,
                    marginRight: "-50%"}}
                to =   {{opacity: 1,
                    marginRight: "8%"}}
                delay={1000}>
                {props => (
                    <div style={props} className="cardContainer">
                        <div className="cardImageContainer">
                            <img className={"cardMedia"}
                                 alt={"random"}
                                 src={this.props.image}/>
                        </div>
                        <div className="bottomColor">
                             <h3 className={"linkHeader"}>
                                {this.props.title}
                             </h3>
                        </div>
                    </div>
                    )}
            </Spring>
        );
    }

    aniShow(){
        return(
            <Spring
                from = {{opacity: 0,
                    marginRight: "-50%"}}
                to =   {{opacity: 1,
                    marginRight: "8%"}}
                delay={1000}>
                {props => (
                    <LinkCard style={props} image={about} title={"about"}/>
                )}
            </Spring>
        );
    }
}

export default LinkCard;
