import React, {Component} from "react";
import {Spring} from "react-spring/renderprops-universal";
import ReactDOM from "react-dom";
import App from "../../App";

var start = "0,0 100,0 100,30 100,70 100,100, 0,100",
	end   = "0,0 70,0 60,40 50,60 40,100 0,100";

class Background extends Component{
	static state;
	constructor(props){
		super(props);
		this.state = {
			run: false
		};
		this.handleClick = this.handleClick.bind(this)
		console.log(this.state);
	}

	handleClick(){
		this.setState({
			run: true
		});
	}

	static startAnim() {
		this.state = true;
		console.log("Pressed button registered");
		console.log(this.state);
	}

	render(){
		console.log("render called")
		if( this.state.run ){
			return(
				<div className="startAnim" style={{backgroundColor: "red", width: "100%", height:"100%"}}>
					<Spring
						from={{ points: start}}
						to={{ points: end}}
						delay={1000}
						config={{duration: 1000 }}>
						{props => (
							<svg className="svgBackground" fill={"#222"} preserveAspectRatio={"none"} viewBox={"0 0 100 100"}>
								<polygon points={props.points}/>
							</svg>
						)}
					</Spring>
				</div>
			);
		}
		else{
			return(
				<div></div>
			);
		}
	}
}

export default Background;
