import React, {Component} from "react"
import MenuRow from "./MenuRow";
import {Spring} from "react-spring/renderprops-universal";
import java from "../../assets/icons/java.svg"
import python from "../../assets/icons/python.svg"
import ml from "../../assets/icons/machine-learning.svg"
import application from "../../assets/icons/application.svg"


class Menu extends Component{
    constructor(props) {
        super(props);
        this.state = {
            hovered: false,
            redirect: false
        };
    }

    render() {
        return(
            <div className={"menuContainer"}>
                <Spring
                    from={{opacity: 0}}
                    to={{opacity: 1}}
                    delay={1500}>
                    {props => (
                        <h1 style={props} className="menuHeader">
                            {this.props.header}
                        </h1>
                    )}
                </Spring>
                <Spring
                    from={{opacity: 0}}
                    to={{opacity: 1}}
                    delay={1800}>
                    {props => (
                        <h2 style={props} className="menuFilter">
                            Filter
                        </h2>
                    )}
                </Spring>
                <svg className="projectLine" viewBox="0 0 100 1">
                    <line x1="0" y1="0" x2="100" y2="00" stroke="#9a0200" strokeWidth="2px" strokeDasharray={100} />
                </svg>
                <div className="menuOptions">
                    <MenuRow manageFilter={this.props.manageFilter} name={"Java"} icon={java} delay={2000}/>
                    <MenuRow manageFilter={this.props.manageFilter} name={"Python"} icon={python} delay={2100}/>
                    <MenuRow manageFilter={this.props.manageFilter} name={"Machine learning"} icon={ml} delay={2200}/>
                    <MenuRow manageFilter={this.props.manageFilter} name={"Application"} icon={application} delay={2300}/>
                </div>

            </div>
        )
    }
}
export default Menu;
