import React, {Component} from "react"
import * as easings from 'd3-ease'
import {Spring} from "react-spring/renderprops-universal";


class MenuRow extends Component{
    constructor(props){
        super(props);
        this.state = {
            hover: true,
            backgroundColor: "#222",
            color: "white",
        };

    }

    hover = () => {
        this.setState({
            hover: true,
            backgroundColor: "#444",
            color: "white",
        });
    };
    notHover = () => {
        this.setState({
            hover: true,
            backgroundColor: "#222",
            color: "white",
        });
    };



    render() {
        return(
            <Spring
                from={{ opacity: 0,
                        background: 'linear-gradient(to right, #009fff, #ec2f4b)'}}
                to  ={{ opacity: 1,
                        background: '#222'}}
                delay={1500}>
                {props => (
                    <div className="rowContainer" style={props}>
                        <div className="row"
                             onMouseOver ={this.hover}
                             onMouseLeave={this.notHover}
                             style={this.state}>
                            <p className="rowName">Row 1</p>
                        </div>
                    </div>
                )}
            </Spring>
        )
    }
}
export default MenuRow;
