import React, {Component} from "react"


class Menu extends Component{
    constructor(props){
        super(props);
        this.state = {
            hover: true,
            backgroundColor: "#222",
            color: "white",
        };
    }

    hover = () => {
        this.setState({
            hover: true,
            backgroundColor: "#222",
            color: "red",
        });
    }
    notHover = () => {
        this.setState({
            hover: true,
            backgroundColor: "#222",
            color: "white",
        });
    }



    render() {
        return(
            <div className={"menuContainer"}>
                <h1 className="menuHeader">
                    Projects
                </h1>
                <div className="menuOptions">
                    <div className="row"
                         onMouseOver ={this.hover}
                         onMouseLeave={this.notHover}
                         style={this.state}>
                        <h3 className="rowName">Row 1</h3>
                    </div>
                    <div className="row">
                        <h3 className="rowName">Row 2</h3>
                    </div>
                    <div className="row">
                        <h3 className="rowName">Row 3</h3>
                    </div>
                    <div className="row">
                        <h3 className="rowName">Row 4</h3>
                    </div>
                    <div className="row">
                        <h3 className="rowName">Row 5</h3>
                    </div>
                    <div className="row">
                        <h3 className="rowName">Row 6</h3>
                    </div>
                    <div className="row">
                        <h3 className="rowName">Row 7</h3>
                    </div>
                </div>
            </div>
        )
    }
}
export default Menu;
