import React, {Component} from "react";
import {Spring} from "react-spring/renderprops-universal";
import ReactDOM from "react-dom";
import App from "../../App";

var start = "0,0 100,0 100,30 100,70 100,100, 0,100",
	end   = "0,0 70,0 60,40 50,60 40,100 0,100";
var firstTime = true;

class Background extends Component{
	state = {
		s: start,
		e: end
	};

	constructor(props){
		super(props);
		if (firstTime) {
			this.state({
				s: start,
				e: end
			});
			firstTime = false;
			console.log("First time entry");
		}
		else{
			this.setState({

			});
			console.log("Second time entry");
		}
		this.startAnim = this.startAnim.bind(this);
	}

	startAnim = () => {
		this.setState({
			s: end,
			e: start
		});

		console.log("Pressed button registered");
		console.log(this.state);
	}

	render(){
		console.log("render called")
		return(
			<div className="startAnima" style={{backgroundColor: "red", width: "100%", height:"100%"}}>
				<h1>
					{this.state.e}
				</h1>
				<Spring
					from={{ points: this.state.s}}
					to={{ points: this.state.e}}
					delay={1000}
					config={{duration: 1000 }}>
					{props => (
						<svg className="svgBackground" fill={"#222"} preserveAspectRatio={"none"} viewBox={"0 0 100 100"}>
							<polygon points={props.points}/>
						</svg>
					)}
				</Spring>
			</div>
		);
	}

}

export default Background;
