import Button from "@material-ui/core/Button";


const paths = {
    start:
        "M0 1080L604.83 1080L1920 1080L1920 0L1368.9 0L0 0L0 1080Z",
    end :
        "M0 1080L518.75 1080L917.64 540L1164.84 426.45L1468.22 0L0 0L0 1080Z",
}

class StartAnimation extends Components{
    gotoNext = () => {
        const { pathIndex } = this.state;
        const nextIndex = pathIndex + 1;
        this.setState({
            pathIndex: nextIndex > pathIds.length - 1 ? 0 : nextIndex
        });
    };



}

