import React, {Component, Fragment} from "react";
import Background from "../backgroundComponent/Background";
import Header from "../headerComponent/Header";
import Area from "../areaComponent/Area";
import rd3 from 'react-d3-library';
import {Spring} from "react-spring/renderprops"

var start = "0,0 70,0 100,0 100,70 100,100, 0,100",
    end   = "0,0 70,0 60,40 50,60 40,100 0,100";

class StartAnimation extends Component{

    render(){
        return(
            <div className="backgroundContainer">
                <Spring
                    from={{ points: start}}
                    to={{ points: end}}
                    delay={2000}>
                    {props => (
                        <svg className="svgBackground" fill={"#222"} preserveAspectRatio={"none"} viewBox={"0 0 100 100"}>
                            <polygon points={props.points}/>
                        </svg>
                    )}
                </Spring>

                <Spring
                    from={{ opacity: 0 }}
                    to={{opacity: 1}}
                    delay={2500}>
                    {props => (
                        <div className="aniHeader" style={props}>
                            <Header/>
                        </div>
                    )}
                </Spring>



            </div>
        );
    }
}



