import React, {Component} from "react";
import {Spring} from "react-spring/renderprops-universal";

class Project extends Component{
    constructor(props){
        super(props);
        this.state = {
            opacity: 1,
            marginTop: "0px",
            pOp: 0,
            backgroundColor: "#222"
        }
    }

    setHover = () => {
        this.setState({
            opacity: .2,
            marginTop: "-200px",
            pOp: 1,
            backgroundColor: "#CCC"
        });
    }
    cancelHover = () => {
        this.setState({
            opacity: 1,
            marginTop: "0px",
            pOp: 0,
            backgroundColor: "#222"
        });
    }

    render() {
        return(
            <Spring
                to={{opacity: this.state.opacity,
                     marginTop: this.state.marginTop,
                     pOp: this.state.pOp,
                     backgroundColor: this.state.backgroundColor}}
            >
                {props => (
                    <div style={{backgroundColor: props.backgroundColor}}
                         className={"projectContainer"}
                         onMouseOver={this.setHover}
                         onMouseLeave={this.cancelHover}>
                        <div className="imageContainer">
                            <img className={"projectImage"}
                                 alt={"random"}
                                 src={this.props.image}
                                 style={{ opacity: props.opacity}}
                            />
                        </div>
                        <div className="projectHeaderContainer">
                            <h2 style={{marginTop: props.marginTop}}
                                className={"projectHeader"}>
                                {this.props.header}
                            </h2>
                        </div>
                        <p style={{opacity: props.pOp}} className={"projectInformation"}>{this.props.modalInfo}</p>
                        <div style={{opacity: props.pOp}} className="projectButtons">
                            <button className="projectButton readMore"></button>
                            <button className="projectButton github"></button>
                        </div>
                    </div>
                    )}
            </Spring>
        );
    }
}

export default Project;

