import React, {Component} from 'react';
import "./assets/css/style.css"
import {Spring, config} from "react-spring/renderprops-universal";

var start = "0,0 100,0 100,30 100,70 100,100, 0,100",
    end   = "0,0 70,0 60,40 50,60 40,100 0,100";

class App extends Component {
    render() {
        return(
            <div>
                <button onClick={() => { this.BG.update()}} style={{position: "absolute"}}>START</button>
                <BG ref={instance => { this.BG = instance; }}/>
            </div>
        );
    }
}

class BG extends Component {
    constructor(props){
        super(props);
        this.state = {
            clicked: false,
            first: start,
            second: end
        };

        this.update = this.update.bind(this)
    }

    update(){
        console.log("Updated");
        if (!this.state.clicked){
            this.setState({
                clicked: true,
                first: end,
                second: start
            })
        }
        else{
            this.setState({
                clicked: false,
                first: start,
                second: end
            })
        }
    }

    ani() {
        return(
            <Spring
                from={{radius: this.state.first}}
                to={{ radius: this.state.second}}
                config={config.wobbly}>
                {props => (
                    <div className="aniHeader" style={props}>
                        <svg preserveAspectRatio={"none"} width={"100vw"} height={"100vh"} viewBox={"0 0 100 100"}>
                            <polygon points={props.radius}/>
                        </svg>
                    </div>
                )}
            </Spring>
        )
    }
    render() {
        console.log("render")
        return(
            <div className={"aniHeader"}>
                {this.ani(this.state.radius)}
            </div>
        );
    }
}

export default App;
