import React, {Component} from "react"
import {Spring, config} from "react-spring/renderprops-universal";
import about from "../../assets/img/about_me.jpg";
import {Redirect} from "react-router-dom";

class LinkCard extends Component{
    constructor(props) {
        super(props);
        this.state = {
            hovered: false,
            redirect: false
        };
    }

    setRedirect = () => {
        this.setState({
            redirect: true
        })
    };
    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to='/about' />
        }
    };
    render() {
        return(
            <Spring
                from = {{opacity: 0,
                    marginRight: "-50%"}}
                to =   {{opacity: 1,
                    marginRight: "8%"}}
                delay={3000}
                config={{duration: 2000}}>
                {props => (
                    <div onClick={this.setRedirect} style={props} className="cardContainer">
                        {this.renderRedirect()}
                        <div className="cardImageContainer">
                            <img className={"cardMedia"}
                                 alt={"random"}
                                 src={this.props.image}/>
                        </div>
                        <div className="bottomColor">
                             <h3 className={"linkHeader"}>
                                {this.props.title}
                             </h3>
                        </div>
                    </div>
                    )}
            </Spring>
        );
    }
}

export default LinkCard;
