import React, {Component} from 'react';
import "./assets/css/style.css"
import {Spring, config} from "react-spring/renderprops-universal";



class App extends Component {

    render() {
        return(
            <div>
                <button onClick={() => { this.BG.update(); }}>START</button>
                <BG ref={instance => { this.BG = instance; }}/>
            </div>
        );
    }
}

class BG extends Component {
    constructor(props){
        super(props);
        this.state = {
            clicked: false,
            before_radius: 30,
            after_radius: 10
        };

        this.update = this.update.bind(this)
    }

    update(){
        console.log("Updated");
        if (!this.state.clicked){
            this.setState({
                clicked: true,
                before_radius: 10,
                after_radius: 30
            })
        }
        else{
            this.setState({
                clicked: false,
                before_radius: 30,
                after_radius: 10
            })
        }
    }

    ani(r) {
        return(
            <Spring
                from={{radius: this.state.before_radius}}
                to={{ radius: this.state.after_radius}}
                delay={1000}
                config={key => (key === 'radius' ? config.slow : config.wobbly)}>
                {props => (
                    <div className="aniHeader" style={props}>
                        <svg preserveAspectRatio={"none"} width={"100%"} height={"100%"} viewBox={"0 0 100 100"}>
                            <circle cx={50} cy={30} r={r}/>
                        </svg>
                    </div>
                )}
            </Spring>
        )
    }
    render() {
        console.log("render")
        return(
            <div className={"aniHeader"}>
                {this.ani(this.state.radius)}
            </div>
        );
    }
}

export default App;
