import React, {Component} from 'react';
import "./assets/css/style.css"
import StartAnimation from "./components/animateComponent/StartAnimation";
import Header from "./components/headerComponent/Header";
import {Spring} from "react-spring/renderprops-universal";



class App extends Component {

    render() {
        return(
            <div>
                <button onClick={() => { this.BG.update(); }}>START</button>
                <BG ref={instance => { this.BG = instance; }}/>
            </div>
        );
    }
}

class BG extends Component {
    constructor(props){
        super(props);
        this.state = {
            clicked: false,
            radius: 40
        };

        this.update = this.update.bind(this)
    }

    update(){
        console.log("Updated")
        if (!this.state.clicked){
            this.setState({
                clicked: true,
                radius: 20
            })
        }
        else{
            this.setState({
                clicked: false,
                radius: 40
            })
        }

    }

    ani() {
        return(
            <Spring
                from={{ opacity: 0 }}
                to={{opacity: 1}}
                delay={1000}>
                {props => (
                    <div className="aniHeader" style={props}>
                        <svg preserveAspectRatio={"none"} width={"100%"} height={"100%"} viewBox={"0 0 100 100"}>
                            <circle cx={50} cy={50} r={this.state.radius}/>
                        </svg>
                    </div>
                )}
            </Spring>
        )
    }
    render() {
        console.log("render")
        return(
            <div className={"aniHeader"}>
                {this.ani()}
            </div>
        );
    }
}

export default App;
