import React, {Component} from "react"

class FilterButton extends Component{
    constructor(props){
        super(props);
        this.state = {
            show : false,
            backgroundColor : "gray"
        }
    }

    onClick = () => {
        this.setState({
            show : true,
            backgroundColor: "orange"
        })
    }

    render() {
        return(
            <button className="filterButton" style={this.state} onClick={ this.showProjects }>{this.props.text}</button>
        );
    }
}

export default FilterButton;
